<?php function mail_create_message($message) {return "<!DOCTYPE html><html style='margin: 0px; padding: 0px; height: 100%; width: 100%'><body style='word-wrap: break-word; background-color: #E6E6E6; padding-top: 20px; padding-bottom: 20px; padding-right: 20px; padding-left: 20px; height: 100%; margin: 0px; font-family: \"Open Sans\", Optima, sans-serif; font-size: 19px'><div style='border: thin solid #858585; background-color: #FFFFFF; padding: 20px'><a href='http://sandrive.tk' style='border: none; outline: none'><img src='http://sandrive.tk/assets/images/logo_main_212.png' alt='San Drive' width='212' border: none; outline: none'></a><br>".$message."</div></body>";} ?>